import React, { useState } from "react";
import * as uuid from "uuid";
import "./App.css";
import NoteForm from "./Components/NoteForm";
import { Container, Row, Col, Button, Card } from "react-bootstrap";

function App() {
  // Declaring state variables
  const [notes, setNotes] = useState([]);
  const [isClicked, setIsClicked] = useState(false);

  function handleAddNote() {
    setIsClicked(true);
  }

  // Cancel the form which adds notes
  function handleCancel() {
    setIsClicked(false);
  }

  // Function for searching notes
  function handleSearch() {}
  function handleSort() {}

  function handleViewdetails() {}
  function handleDelete() {}

  //Adding the note plus id
  function handleSubmittedNote(note) {
    note.id = uuid.v4();
    setNotes([...notes, note]);
  }

  return (
    <Container>
      <Row>
        <Col>
          {notes.map((note) => (
            <div>
              <h1>{note.title}</h1>
              <h3>{note.descriptio}</h3>
            </div>
          ))}
        </Col>
        <Col className="column">
          <div className="logo">My Notes</div>
          <div>
            <Button className="addButton" onClick={handleAddNote}>
              Add new Note
            </Button>
          </div>
          {isClicked ? (
            <NoteForm
              handleSubmittedNote={handleSubmittedNote}
              handleCancel={handleCancel}
            />
          ) : (
            <div />
          )}
        </Col>
      </Row>
    </Container>
  );
}

export default App;
