import React from "react";
import { useFormik } from "formik";
import * as Yup from "yup";
import Alert from "react-bootstrap/Alert";

export default function NoteForm(props) {
  const formik = useFormik({
    initialValues: {
      title: "",
      description: "",
      categories: "",
    },

    // Responsible for input validation
    validationSchema: Yup.object().shape({
      title: Yup.string()
        .max(10, "Max number of characters")
        .required("Provide the title"),
      description: "",
      categories: "",
    }),
    onSubmit: () => props.handleSubmittedNote(formik.values),
  });
  return (
    <form onSubmit={formik.handleSubmit}>
      <div className="div">
        <input
          name="title"
          type="text"
          placeholder="Title"
          onChange={formik.handleChange}
          value={formik.values.title}
        />
      </div>
      <div className="div">
        <select
          name="categories"
          placeholder="categories"
          onChange={formik.handleChange}
          value={formik.values.categories}
        >
          <option value="computer_networks">Computer Networks</option>
          <option value="digital_logic">Digital Logic</option>
          <option value="data_structures">Data Structures</option>
        </select>
      </div>
      <div className="div">
        <input
          name="description"
          type="text"
          placeholder="Description"
          onChange={formik.handleChange}
          value={formik.values.description}
        />
      </div>
      <div className="div">
        {formik.errors.title ? (
          <Alert color="primary">{formik.errors.title}</Alert>
        ) : (
          ""
        )}
      </div>
      <button type="submit" onClick={formik.handleSubmit}>
        Submit
      </button>
      <button type="button" onClick={formik.resetForm}>
        Reset
      </button>
      <button type="button" onClick={props.handleCancel}>
        Cancel
      </button>
    </form>
  );
}
